	public final static function getCaptionLabelFromPropertyName($propertyName)	{
		/*
		INPUT nextRegistrationNumber 
		OUTPUT Next Registration Number
		*/
		$propertyArray = preg_split('/(?=[A-Z])/', $propertyName);
