		/*
		doc refer to the outer node 
		if pos = *, you will get all matched nodes from the outer node 
		else specific position
		*/
		$pos = trim("".$pos);
		$nodeList = array();
		$tempList = $doc->getElementsByTagName($nodename);
		if ($pos == "*")	{
			//All records 
			foreach ($tempList as $alist)	{
