			$line .= "\n";
			fwrite($file1, $line) or Object::shootException("Could not write to Line $currentLineNumber");
			$currentLineNumber++;
		}
		//Now writing data 
		for ($i=0; ($i < sizeof($this->dataLines)) && ($currentLineNumber >= 17); $i++, $currentLineNumber++)	{
			$dataLine1 = $this->dataLines[$i];
