		$storedDocumentHashValue = trim(self::getDOMChecksum($doc));
		$calculatedDocumentHashValue = trim(self::calculateDOMChecksum($doc));
		return (strcmp($calculatedDocumentHashValue, $storedDocumentHashValue) == 0);
	}
